import { generateId } from '../utils/helpers'
import axios from 'axios'

export let users = {
  'Mike': {
    id: 'Mike',
    avatar: 'https://randomuser.me/api/portraits/men/49.jpg',
    createdPolls: ['98c1e68-3320-4157-18w7-84c2d1e7zxf1v'],
    answeredPolls: [],
    points: 1
  },
  'Carol': {
    id: 'Carol',
    avatar: 'https://randomuser.me/api/portraits/women/2.jpg',
    createdPolls: [],
    answeredPolls: [],
    points: 0
  },
  'Jennifer': {
    id: 'Jennifer',
    avatar: 'https://randomuser.me/api/portraits/women/3.jpg',
    createdPolls: [],
    answeredPolls: ['17w1v628-192q-1537-12x4-11a131j3tb6p'],
    points: 1
  },
  'Victor': {
    id: 'Victor',
    avatar: 'https://randomuser.me/api/portraits/men/16.jpg',
    createdPolls: [],
    answeredPolls: [],
    points: 0
  }
}


function formatPoll ({ firstQuestion, secondQuestion, author }) {
  return {
    author,
    id: generateId(),
    firstQuestion,
    secondQuestion,
    isAnswered: false,
    firstQuestionReplies: 0,
    secondQuestionReplies: 0,
    totalReplies: 0
  }
}

export async function _savePoll ({ firstQuestion, secondQuestion, author }) {
  return new Promise((res, rej) => {
    const formattedPoll = formatPoll({
      author,
      firstQuestion,
      secondQuestion
    })

    try {
      axios.post('http://localhost:5000/api/polls', formattedPoll)
      .then((res) => {
      console.log(res)
      })

      axios.put('http://localhost:5000/api/users')
        
      res(formattedPoll)
    }
    catch(err) {
      console.log(err)
    }

  })
}

export const _getUsers = async () => {
  try {
    const res = await axios.get('http://localhost:5000/api/users')
    return res.data
  }
  catch(err) {
    console.log(err)
  }
}

export const _getPolls = async () => {
  try {
    const res = await axios.get('http://localhost:5000/api/polls')
    return res.data
  }
  catch(err) {
    console.log(err)
  }
}
