import { RECEIVE_USERS, ADD_NEW_POLL_TO_USER } from '../actions/users'

export default  function users (state = {}, action) {
  switch(action.type) {
    case RECEIVE_USERS :
      return {
        ...state,
        ...action.payload.users
      }
      case ADD_POLL_TO_USER :
      return {
        ...state,
        [action.payload.user.id]: {
          ...state[action.payload.user.id],
          polls: state[action.payload.user.id].polls.concat(action.payload.pollId)
        }
      }
    default :
      return state
  }
}
