import { generateId } from '../utils/helpers'
export const ADD_POLL = 'ADD_POLL'

function addPoll(poll) {
  return {
    type:  ADD_POLL,
    payload: {
      poll
    }
  }
}


// Working on this!
export function handleAddPoll(firstQuestion, secondQuestion) {
  return (dispatch, getState) => {
    const { authedUser } = getState()

    return savePoll({
      author: authedUser,
      firstQuestion,
      secondQuestion    
    })
  }
}

export function savePoll (info) {
  return _savePoll(info)
}

export function _savePoll ({ firstQuestion, secondQuestion, author }) {
  return new Promise((res, rej) => {
    const formattedPoll = formatPoll({
      author,
      firstQuestion,
      secondQuestion
    })

    setTimeout(() => {
      tweets = {
        ...tweets,
        [formattedPoll.id]: formattedPoll,
      }

      users = {
        ...users,
        [author]: {
          ...users[author],
          tweets: users[author].tweets.concat([formattedPoll.id])
        }
      }

      res(formattedPoll)
    }, 1000)
  })
}

function formatPoll ({ firstQuestion, secondQuestion, author }) {
  return {
    author,
    id: generateId(),
    firstQuestion,
    secondQuestion
  }
}
