          // : null
        }
        {/* {pollIds ? pollIds.map((pollId) => {
            return <Poll key={pollId} id={pollId}/>
          }) : null} */}
      </div>
    </div>
  )
}

function mapStateToProps ({ authedUser, polls }) {
  return {
    authedUser,
    polls,
    pollIds: Object.keys(polls)
  }
}

export default connect(mapStateToProps)(HomePanel)
