import React, { useState } from 'react'
import { connect } from 'react-redux'
import { Button, Form } from 'semantic-ui-react'
import { Link } from 'react-router-dom'

const PollAnswer = ({ authedUser, data }) => {
  const [option, setOption] = useState('')

  const handleChange = e => {
    console.log(e.target.textContent)
  }

  return (
    <div className='answer-poll'>
      <div className='answer-poll__head'>
        <div className='answer-poll__head-left'>
          <img src={authedUser.avatar} className='answer-poll__head-left__img shadow-xs'/>
        </div>
        <div className='answer-poll__head-right'>
          <span className='answer-poll__head-right__name'>{authedUser.id}</span>
        </div>
      </div>
      <div className='answer-poll__body'>
        
        <Form>
          <Form.Group>
            <Form.Radio
              label={data.firstQuestion}
              value={data.firstQuestion}
              onChange={handleChange}
            />  
            <Form.Radio
              label={data.secondQuestion}
              value={data.secondQuestion}
              onChange={handleChange}
            />         
          </Form.Group>
        </Form>

        <p className='answer-poll__body-title'>Would you rather...</p>
        <p className='answer-poll__body-questions'>{data.firstQuestion}<br/>or... <br/>{data.secondQuestion}
        </p>
        <Button to={`/home`} className='answer-poll__body-btn'>Reply</Button>
      </div>
    </div>
  )
}

function mapStateToProps({ authedUser, polls }) {
  return {
    authedUser,
    polls
  }
}

export default connect(mapStateToProps)(PollAnswer)
