import React from 'react'
import { connect } from 'react-redux'
import Poll from './Poll'


const AnswerPoll = ({ id, authedUser, polls }) => {
  console.log(id)
  return (
    <>
      {polls.filter(poll => poll.id === id).map((filteredPoll) => {
        return <AnswerPoll key={filteredPoll.id} data={filteredPoll}/>
      })}
    </>
  )
}

function mapStateToProps({ authedUser, polls }) {
  return {
    authedUser,
    polls
  }
}

export default connect(mapStateToProps)(AnswerPoll)
