import { CREATE_POLL } from '../actions/polls'
import { generateId } from '../utils/helpers'

export default function polls (state = [], action) {
  switch(action.type) {
    case CREATE_POLL :
      return {
        ...state,
        arr: state.arr.concat
      }
    default :
      return state
  }
}
