import React from 'react'
import { connect } from 'react-redux'
import Nav from '../components/Nav'
import AnswerPoll from '../components/AnswerPoll'
import AnswerPollPanel from '../components/AnswerPollPanel'

const PollPage = ({ id, polls }) => {
  return (
    <>
      <Nav />
      <div className='answer-poll-panel main-container'>
      {polls.filter(poll => poll.id === id).map((filteredPoll) => {
        return <AnswerPoll key={filteredPoll.id} data={filteredPoll}/>
      })}
      </div>
      <AnswerPollPanel id={id}/>
    </>
  )
}

function mapStateToProps({ authedUser, polls }, props) {
  const { id } = props.match.params

  return {
    id,
    authedUser,
    polls
  }
}

export default connect(mapStateToProps)(PollPage)
