import React from 'react'
import { connect } from 'react-redux'

const AnswerPollPanel = ({ id, polls }) => {
  return (
    <div className='answer-poll-panel'>
      {polls.filter(poll => poll.id === id).map((filteredPoll) => {
        return <AnswerPoll key={filteredPoll.id} data={filteredPoll}/>
      })}
    </div>
  )
}

function mapStateToProps({ authedUser, polls }) {
  return {
    authedUser,
    polls
  }
}

export default connect(mapStateToProps)(AnswerPollPanel)
