export const RECEIVE_USERS = 'RECEIVE_USERS'
export const ADD_POLL_TO_USER = 'ADD_POLL_TO_USER'

export function receiveUsers(users) {
  return {
    type: RECEIVE_USERS,
    payload: {
      users
    }  
  }
}

export function addPollToUser(user, pollId) {
  return {
    type: ADD_POLL_TO_USER,
    payload: {
      user,
      pollId
    }  
  }
}
