import { generateId } from '../utils/helpers'
export const ADD_POLL = 'ADD_POLL'

function addPoll(poll) {
  return {
    type:  ADD_POLL,
    payload: {
      poll
    }
  }
}


// Working on this!
export function handleAddPoll(firstQuestion, secondQuestion) {
  return (dispatch, getState) => {
    const { authedUser } = getState()

    return savePoll({
      id: generateId(),
      author: authedUser,
      firstQuestion,
      secondQuestion    
    })
  }
}

export function savePoll (info) {
  return _savePoll(info)
}

export function _savePoll ({ firstQuestion, author, replyingTo }) {
  return new Promise((res, rej) => {
    const formattedTweet = formatTweet({
      text,
      author,
      replyingTo
    })

    setTimeout(() => {
      tweets = {
        ...tweets,
        [formattedTweet.id]: formattedTweet,
      }

      users = {
        ...users,
        [author]: {
          ...users[author],
          tweets: users[author].tweets.concat([formattedTweet.id])
        }
      }

      res(formattedTweet)
    }, 1000)
  })
}
