import { ADD_POLL } from '../actions/polls'
import { RECEIVE_POLLS } from '../actions/polls'


export default function polls (state = [], action) {
  switch(action.type) {
    case RECEIVE_POLLS :
      return
    case ADD_POLL :
      return [
        ...state.concat(action.payload)
      ]
    default :
      return state
  }
}
