import { CREATE_POLL } from '../actions/poll'
import { generateId } from '../utils/helpers'

export default function polls (state = {}, action) {
  switch(action.type) {
    case CREATE_POLL :
      return {
        id: generateId(),
        firstQuestion,
        secondQuestion
      }
    default :
      return state
  }
}
