import { generateId } from '../utils/helpers'
export const CREATE_POLL = 'CREATE_POLL'

export function createPoll(firstQuestion, secondQuestion, author) {
  return {
    type: CREATE_POLL,
    payload: {
      id: generateId(),
      author,
      firstQuestion,
      secondQuestion
    }
  }
}
