import { ADD_POLL, RECEIVE_POLLS, ANSWER_POllS } from '../actions/polls'

export default function polls (state = {}, action) {
  switch(action.type) {
    case RECEIVE_POLLS :
      return {
        ...state,
        ...action.payload.polls
      }
    case ADD_POLL :
      return {
        ...state,
        [action.payload.poll.id]: action.payload.poll
      }
    case ANSWER_POLL :
      return {
        ...state,
        [action.payload.id]: {
          is
        }
      }
    default :
      return state
  }
}
