import { generateId } from '../utils/helpers'
export const CREATE_POLL = 'CREATE_POLL'

export function createPoll(firstQuestion, secondQuestion) {
  return (dispatch, getState) => {
    const { authedUser } = getState
    dispatch({
      type: CREATE_POLL,
      payload: {
        id: generateId(),
        author: authedUser,
        firstQuestion,
        secondQuestion
      }
    })
 
  }
  
}
